// n8n Code node (JavaScript)
// Entrada esperada: items com item.json.output (string)
// Sa√≠da: item.json = { text, catboxLinks, hasCatbox }

const items = $input.all();

// Regex para capturar links do Catbox (com ou sem "files.")
const catboxRegex = /https?:\/\/(?:files\.)?catbox\.moe\/[^\s\]]+/gi;

// Remove blocos [IMAGEM: ...] (com qualquer URL) do texto
const removeImagemBlocks = (str) =>
  str.replace(/\[IMAGEM:\s*https?:\/\/[^\]]+\]/gi, "");

return items.map(({ json }) => {
  const raw = (json?.output ?? "").toString();

  // 1) Captura todos os links Catbox
  const catboxLinks = [...raw.matchAll(catboxRegex)].map((m) => m[0]);

  // 2) Remove blocos [IMAGEM: ...]
  let text = removeImagemBlocks(raw);

  // 3) Remove qualquer link Catbox que esteja solto no texto
  text = text.replace(catboxRegex, "");

  // 4) Limpeza de espa√ßos/quebras
  text = text
    .replace(/[ \t]+\n/g, "\n") // espa√ßos antes de quebras
    .replace(/\n{3,}/g, "\n\n") // muitas quebras seguidas
    .replace(/[ \t]{2,}/g, " ") // espa√ßos duplos
    .trim();

  return {
    json: {
      text,
      catboxLinks,
      hasCatbox: catboxLinks.length > 0,
    },
  };
});

// Acessa o item atual. Se o n√≥ "catbox dados" for o anterior,
// o dado "wa_chatid" estar√° dentro do item atual.

const chatid = $json.wa_chatid;

// 1. Remove "@s.whatsapp.net" e todos os caracteres n√£o-d√≠gitos para limpar.
// O ".replace(/\D/g, '')" substitui qualquer coisa que n√£o seja d√≠gito por nada.
const cleanedNumber = chatid.replace('@s.whatsapp.net', '').replace(/\D/g, '');

// 2. Verifica o tamanho (12 d√≠gitos) para adicionar o "9" extra.
let formattedNumber = cleanedNumber;

if (cleanedNumber.length === 12) {
  // Adiciona o '9' no local correto (ap√≥s o DDD)
  formattedNumber = cleanedNumber.substring(0, 4) + '9' + cleanedNumber.substring(4);
}

// Retorna um novo objeto com o telefone formatado.
return {
    json: {
        telefone_formatado: formattedNumber
    }
};

return items.map(item => {
  const raw = item.json?.body?.chat?.wa_chatid || '';
  const digits = raw.split('@')[0].replace(/\D/g,'');
  const m = digits.match(/^(\d{2})(\d{2})(\d{8,9})$/);
  let normalized = digits;
  if (m) {
    const [, country, ddd, local] = m;
    normalized = (local.length === 8) ? (country + ddd + '9' + local) : (country + ddd + local);
  }
  item.json.wa_chatid_normalized = normalized;
  return item;
});
