// Entrada esperada: items com item.json.output (string)
// SaÃ­da: item.json = { text, catboxLinks, hasCatbox }

const items = $input.all();

// Regex para capturar links do Catbox (com ou sem "files.")
const catboxRegex = /https?:\/\/(?:files\.)?catbox\.moe\/[^\s\]]+/gi;

// Remove blocos [IMAGEM: ...] (com qualquer URL) do texto
const removeImagemBlocks = (str) =>
  str.replace(/\[IMAGEM:\s*https?:\/\/[^\]]+\]/gi, "");

return items.map(({ json }) => {
  const raw = (json?.output ?? "").toString();

  // 1) Captura todos os links Catbox
  const catboxLinks = [...raw.matchAll(catboxRegex)].map((m) => m[0]);

  // 2) Remove blocos [IMAGEM: ...]
  let text = removeImagemBlocks(raw);

  // 3) Remove qualquer link Catbox que esteja solto no texto
  text = text.replace(catboxRegex, "");

  // 4) Limpeza de espaÃ§os/quebras
  text = text
    .replace(/[ \t]+\n/g, "\n") // espaÃ§os antes de quebras
    .replace(/\n{3,}/g, "\n\n") // muitas quebras seguidas
    .replace(/[ \t]{2,}/g, " ") // espaÃ§os duplos
    .trim();

  return {
    json: {
      text,
      catboxLinks,
      hasCatbox: catboxLinks.length > 0,
    },
  };
});

